import React, {useState, useEffect} from 'react';

const MessageList = function() {
    const [messageList, setMessageList] = useState([])
    const [textValue, setTextValue] = useState('')
    const [authorValue, setAuthorValue] = useState('')

    const botName = 'Бот'
    const botMessage = 'сообщение бота'

    function changeMessageList() {
        const newMessageList = [...messageList, {textValue, authorValue}]
        setMessageList(newMessageList)
    }

    useEffect(() => {
        if (messageList.length % 2 === 1) {
            return () => {
                const newMessageList = [...messageList, {botMessage, botName}]
                setMessageList(newMessageList)
            }
        }
    }, [messageList])

    return (
        <div>
            <input
                type="text"
                value={textValue}
                onChange={event => setTextValue(event.target.value)}
                placeholder="text..."
            />
            <input
                type="text"
                value={authorValue}
                onChange={event => setAuthorValue(event.target.value)}
                placeholder="Author..."
            />
            <button onClick={changeMessageList}>Add</button>
            <ul>
                {
                    messageList.map((item) => <li>
                        <span className="author">{item.authorValue}: </span>{item.textValue}
                    </li>)
                }
            </ul>
        </div>
    )
}

export default MessageList;
